	public static final boolean isValidCSF( String input ){
		Scanner scan = new Scanner(input);
		while(scan.hasNextLine())
		{
			String next = scan.nextLine();
			if(!next.matches("[.a-zA-Z0-9]+(\\s*:\\s*)[^\\n]+"))
				return false;
		}
		return true;
	}
	
